import { useState } from 'react'
import Display from './Display'
import Buttons from './Buttons'

const App = () => {
  const [feedback, setFeedback] = useState({ good: 0, neutral:0, bad: 0 });
  const [clickHistory, setClickHistory] = useState([]);
  const handleClick = (typeClick) => {
    setClickHistory([...clickHistory, typeClick[0]]);
    switch (typeClick) {
      case 'GOOD':
        setCounter({ ...counter, left: counter.left + 1 })
        break;
      case 'NEUTRAL':
        setCounter({ ...counter, right: counter.right + 1 })
        break;
      case 'BAD':
        setCounter({ ...counter, right: counter.right + 1 })
        break;
      default:
        break;
    }
  }

  const reset = () => {
    setCounter({ left: 0, right: 0 });
    setClickHistory([]);
  }

  return (
    <div>
      <Display counterLeft={counter.left} counterRight={counter.right} clickHistory={clickHistory} />
      <Buttons onClick={() => handleClick('LEFT')} text="Left" />
      <Buttons onClick={() => reset()} text="Reset" />
      <Buttons onClick={() => handleClick('RIGHT')} text="Right" />
    </div>
  )
}

export default App
